limits.map:
function (doc) {
  function isLimits(doc) {
    var suffix = "/limits";
    return doc._id.indexOf(suffix, doc._id.length - suffix.length) !== -1;
  };
  if (isLimits(doc)) {
    emit(doc._id.slice(0, -7), doc);  
  }
}

