packages-all.map:
function (doc) {
  var isPublicPackage = function(doc) { 
    return Object.keys(doc.binding).length == 0 && doc.publish;
  }

  if (isPublicPackage(doc)) try {
    var date = new Date(doc.start || doc.updated);
    emit([date.getTime(), doc.name], {namespace: doc.namespace, name: doc.name, version: doc.version, publish: doc.publish, annotations: doc.annotations, binding: doc.binding});
  } catch (e) {}
}

rules-with-status.map:
function (doc) {
  var PATHSEP = "/";

  var isTrigger = function (doc) { return (doc.exec === undefined && doc.binding === undefined && doc.parameters !== undefined) };
  var isRule = function (doc) {  return (doc.trigger !== undefined) };

  if (isRule(doc)) try {
    emit([doc.namespace,doc.name], {namespace: doc.namespace, name: doc.name, version: doc.version, publish: doc.publish, annotations: doc.annotations, updated: doc.updated, action: doc.action, trigger: doc.trigger });
  } catch (e) {}

  if (isTrigger(doc)) try {
    if(doc.rules){
      for (var rule in doc.rules) {
        var temp = rule.split(PATHSEP);
        emit([temp[0], temp[1]], {"status" : doc.rules[rule].status } );                  
      }          
    }    
  } catch (e) {}
}

rules-with-status.reduce:
function (keys, values, reduce) {
  if(reduce){
    var merged = [].concat.apply([], values);
    if (merged.length == 1) {
      return merged[0];
    } else if(merged.length > 1){
      for (var attrname in merged[1]) { merged[0][attrname] = merged[1][attrname]; }
      return merged[0];
    }
  }
  return values;
}

bindings.map:
function (doc) {
  var PATHSEP = '/';
  var isPackage = function (doc) {  return (doc.binding !== undefined) };

  if (isPackage(doc) && Object.keys(doc.binding).length !== 0) try {
    var date = new Date(doc.start || doc.updated);
    emit([doc.namespace, date.getTime(), doc.name], {namespace: doc.namespace, name: doc.name, version: doc.version, publish: doc.publish, annotations: doc.annotations, binding: doc.binding});
  } catch (e) {}
}

actions.map:
function (doc) {
  var PATHSEP = "/";
  var isAction = function (doc) { return (doc.exec !== undefined) };

  if (isAction(doc)) try {
    var ns = doc.namespace.split(PATHSEP);
    var root = ns[0]; ns.shift();
    var date = new Date(doc.start || doc.updated);
    emit([doc.namespace, date.getTime(), doc.name], {namespace: doc.namespace, name: doc.name, version: doc.version, publish: doc.publish, annotations: doc.annotations});
    if (root !== doc.namespace) {
      emit([root, date.getTime(), doc.name], {namespace: doc.namespace, name: doc.name, version: doc.version, publish: doc.publish, annotations: doc.annotations});
    }
  } catch (e) {}
}

triggers-count.map:
function (doc) {
  var PATHSEP = '/';
  var isTrigger = function (doc) { return (doc.exec === undefined && doc.binding === undefined && doc.parameters !== undefined) };

  if (isTrigger(doc)) try {
    var ns = doc.namespace.split(PATHSEP);
    var root = ns[0]; ns.shift();
    emit(doc.namespace, 1);
    if (root !== doc.namespace) {
      emit(root, 1);
    }
  } catch (e) {}
}

